from mpython import *import networkimport usocketmy_wifi = wifi()my_wifi.connectWiFi("WIFI名","WIFI密码")s = usocket.socket(usocket.AF_INET, usocket.SOCK_STREAM)oled.fill(0)oled.DispChar(my_wifi.sta.ifconfig()[0],0,0,1)oled.show()s.bind(("",1145))s.listen(30)while True:    con = s.accept()[0]    data = con.recv(1024)    fbuf = framebuf.FrameBuffer(bytearray(data),128,64,framebuf.MONO_HLSB)    oled.fill(0)    oled.blit(fbuf,0,0)    oled.show()    con.close()